// pdfUtils.js

import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';

// Definir generatePDFBase
export const generatePDFBase = (doc, title, creationDate) => {
    //RECTANGULO CON TITULO PROOFIMASTER
    doc.setFillColor(255, 128, 0);
    doc.rect(220, 20, 50, 10, 'F');
    doc.setTextColor(0);
    doc.setFontSize(12);
    doc.text("PROOFIMASTER", 225, 26);

    //TITULO TIPO INFORME
    doc.setFont('helvetica', 'bold');
    doc.setFontSize(25);
    doc.setTextColor(0, 0, 0);
    doc.text(title, 165, 70);

    //RECTANGULO CON FECHA CREACION
    doc.setFillColor(255, 128, 0);
    doc.rect(220, 35, 50, 15, 'F');
    doc.setTextColor(0);
    doc.setFontSize(12);
    doc.text("Fecha creaci√≥n", 225, 42);
    doc.setFont('helvetica', 'normal');
    doc.text(creationDate, 225, 48);

    var imgData = 'data:image/png;base64,';

    //LOGO PROOFISILLAS
    doc.addImage(imgData, 'PNG', 20, 10, 50, 50);

    //DATOS EMPRESARIALES
    doc.setFontSize(10);
    doc.setTextColor(0);
    doc.text("proofisillas ltda.", 20, 60);
    doc.text("NIT: 1234567890", 20, 65);
};

// Definir generateTable
export const generateTable = (doc, head, body) => {
    autoTable(doc, {
        startY: 75,
        head: [head],
        body: body,
        headStyles: {
            fillColor: [255, 128, 0],
            textColor: [0, 0, 0],
            fontStyle: 'bold',
        },
        bodyStyles: {
            fillColor: [220, 220, 220],
            textColor: [0, 0, 0],
        },
        alternateRowStyles: {
            fillColor: [240, 240, 240]
        },
    });
};

// Definir Footer
export const addFooter = (doc) => {
    const pageHeight = doc.internal.pageSize.height;
    const pageWidth = doc.internal.pageSize.width;
    
    // Texto de agradecimiento
    doc.setTextColor(255, 128, 0);
    doc.setFontSize(15);
    doc.text("Gracias por usar Proofimaster", pageWidth / 2, pageHeight - 20, { align: "center" });

    // L√≠nea horizontal
    doc.setDrawColor(0); // Color negro
    doc.setLineWidth(0.5);
    doc.line(10, pageHeight - 18, pageWidth - 10, pageHeight - 18); // De izquierda a derecha, dejando un margen de 10

    // Datos de contacto
    doc.setTextColor(255, 128, 0); // Texto en color negro
    doc.setFontSize(12);
    
    // Izquierda
    doc.text("Calle 48 A No. 28-26 Sur", 10, pageHeight - 10);
    
    // Centro
    doc.text("info@proofisillas.com", pageWidth / 2, pageHeight - 10, { align: "center" });
    
    // Derecha
    doc.text("321 ¬∑ 224 97 27", pageWidth - 10, pageHeight - 10, { align: "right" });
};

